// ** Redux Imports
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

// ** API Import
import {getAllProduct} from "../../api";

export const fetchProducts = createAsyncThunk(
  "appProducts/fetchProducts",
  async () => {
    const response = await getAllProduct();
    const datas = response.data.data;
    return datas;
  }
);

// export const fetchActivitybyid = createAsyncThunk(
//   "appTodo/fetchActivitybyid",
//   async (id) => {
//     const response = await getActivitybyid(`/${id}`);
//     const datas = response.data;
//     return {
//       datas,
//       id,
//     };
//   }
// );

export const appProductSlice = createSlice({
  name: "appProducts",
  initialState: {
    dataProducts: [],
    // taskid: {},
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchProducts.fulfilled, (state, action) => {
        state.tasks = action.payload;
      })
      // .addCase(fetchActivitybyid.fulfilled, (state, action) => {
      //   state.taskid = action.payload;
      // });
  },
});

export const {} = appProductSlice.actions;

export default appProductSlice.reducer;
