import React, { useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { fetchProductsId } from "../store";

function Index() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { id } = useParams();
  useEffect(() => {
    dispatch(fetchProductsId(id));
  }, [dispatch]);

  const store = useSelector((state) => state.Product.dataProductsId);
  console.log(store);

  return (
    <div>
      <div className="grid grid-cols-2 gap-x-4 gap-y-8 mt-20">
        <div className="container flex justify-center ">
          <div className="max-w-sm py-10">
            <div className="bg-white relative shadow-lg hover:shadow-xl transition duration-500 rounded-lg h-[400px]">
              <img src={store?.datas?.thumbnail} alt="img" className="h-[400px] " />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Index;
